import{B as n,_ as l,h as r,o as i,c as h,a as t,e,i as o,w as s,b as c}from"./index.js";const d={setup(){n({title:"Homebrew Rules"})}},u=t("p",null," I use a wide variety of homebrew rules at my table. Ranging from small changes to classes and races, to variations on how parts of the game are played. This page is an (incomplete) list of the rules currently in use at my gaming table. If you are running a game in the setting you have the choice of what homebrew rules you want to use (with the consent of your players). Remember, in general this sort of thing should be discussed at session 0. Many of these come from other sources, though I can't necessarily remember what they all are. ",-1),p=t("p",null," New homebrew rules tend to go through the following stages: ",-1),g=t("ol",null,[t("li",null,"Initial Design: At this point I am still deciding if I want or need a new rule or feature for a particular purpose, what I want it to accomplish, and what general shape it should take."),t("li",null,"Proposal: After the initial design is written and ready I present it to my players for a readthrough and feedback. I answer questions about my intent, and make alterations based on their feedback."),t("li",null,"Play Test: Most rules will be tried first in a one shot session or implemented on a trial basis before long-term acceptance."),t("li",null,"Adoption: The rule is adopted by the table for general use.")],-1),f=t("h2",null,"Rules Currently In Use",-1),m=t("h3",null,"General Rules",-1),b=t("strong",null,"Character Factions.",-1),_=t("p",null,[t("strong",null,"Group Skill Checks."),e(" Skill checks that require everyone to succeed to be a success (such as moving stealthily as a group) are made by averaging the result. So everyone in the party roles their dice, adds their modifiers, and then the results are averaged to determine if the group succeeds or fails. Skill checks that require only one person in the group to succeed should only have one person roll. That person can roll with advantage if more than one party member is attempting to do the action. The players can choose who among them makes the check.")],-1),y=t("strong",null,"Injuries.",-1),w=t("p",null,[t("strong",null,"Somatic and Material Components."),e(" The hand holding your spell focus can be used for somatic components of spells, in addition to the spell focus replacing the need for material components without a cost.")],-1),v=t("p",null,[t("strong",null,"Sources Allowed."),e(" I allow players to use any feat, class, spell, or background published in an official WotC book for 5th edition that is not designed for a specific setting or adventure. In general this means they must come from one of the following books: The Player's Handbook, Dungeon Master's Guide, Monster Manual, Volo's Guide to Monsters, Xanathar's Guide to Everything, Mordenkainen's tome of Foes, Tasha's Cauldron of Everything, Fizban's Treasure of Dragons, or Monsters of the Multiverse. Character Races must come from the related page on this site.")],-1),k=t("strong",null,"Strongholds.",-1),S=t("p",null,[t("strong",null,"Tool Use and Ability Checks."),e(" If an ability check requires the use of a set of tools listed in the Players Handbook, proficiency in the tool is used instead of proficiency in any other skill. For example, when attempting to pick a lock with thieves' tools, use thieves tools proficiency, slight of hand does not apply. Which ability to use continues to depend on what exactly the player is doing.")],-1),C=t("h3",null,"Class Changes",-1),T=t("p",null,[t("strong",null,"All Classes."),e(" All classes have access to the bonus features presented in Tasha's Cauldron of Everything.")],-1),R=t("p",null,[t("strong",null,"Barbarian: Less Exhausting Frenzy."),e(" Frenzy does not cause exhaustion the first time it is used after a long rest.")],-1),A=t("p",null,[t("strong",null,"Barbarian: Strength Based Intimidating Presence."),e(" The Barbarians Intimidating Presence feature has a DC equal to 8 + proficiency + strength modifier.")],-1),I=t("strong",null,"Cleric: Homebrew Domains.",-1),x=t("strong",null,"Factotum: New Class.",-1),B=t("strong",null,"Fighter: Scourer Subclass.",-1),M=t("strong",null,"Fighter: Eldritch Knight Spell List",-1),D=t("strong",null,"Ranger: Bonus Spells Known.",-1),W=t("strong",null,"Sorcerer: Bonus Spells Known.",-1),E=t("strong",null,"Sorcerer: Sorceron Bloodline",-1),F=t("p",null,[t("strong",null,"Warlock: Bonus Spells Known."),e(" Warlocks Expanded Spell List spells are known automatically and do not count against their maximum spells known.")],-1),H=t("strong",null,"Wizard: Expanded Spellbook.",-1),q=t("h3",null,"Race Changes",-1),P=t("strong",null,"All Races: Languages.",-1),z=t("strong",null,"All Races: Age.",-1),j=t("strong",null,"Child of Molton: Custom Race.",-1),G=t("strong",null,"Dwarf: Subraces.",-1),N=t("strong",null,"Elf: Subraces.",-1),K=t("strong",null,"Faelin: Custom Race.",-1),L=t("strong",null,"Gnomes: Renamed Subraces.",-1),O=t("strong",null,"Half-Dwarf: Custom Race.",-1),V=t("strong",null,"Halfling: Tallfellow Subrace.",-1),$=t("p",null,[t("strong",null,"Half-Orc: Removed."),e(" The half-orc race is not available for players to choose.")],-1),J=t("strong",null,"Human: Custom Subraces.",-1),Q=t("strong",null,"Kobold: No Sunlight Sensitivity.",-1),U=t("strong",null,"Orcs: Rebuilt Race",-1),X=t("strong",null,"Sorceron Blooded: Custom Race.",-1),Y=t("strong",null,"Tieflings: Custom Subraces.",-1),Z=t("strong",null,"Trollkin: Custom Race.",-1),ee=t("h3",null,"Spells",-1),te=t("strong",null,"Force Weapon: Custom Spell.",-1),ae=t("p",null,[t("strong",null,"Heal Minor Injury: Custom Spell."),e(" A 3rd level spell meant to be paired with the injury table, it can heal physical injuries that do not involve missing body parts or severe brain damage such as broken bones, bleeding wounds etc.")],-1),oe=t("h2",null,"Rules Currently in Testing",-1),se=t("p",null," These changes are currently in the proposal or play test stages. ",-1),ne=t("h3",null,"General Rules",-1),le=t("strong",null,"Advanced Weapons Table.",-1),re=t("em",null,"better",-1),ie=t("em",null,"current weapons table",-1),he=t("p",null,[t("strong",null,"Initiative Jumping."),e(" If a particular action would trigger intiative (such as declaring an attack during a social encounter, or attacking an enemy from stealth). The character or creature that declared that action does not roll initiative, but is instead automatically at the top of the initiative order before other initiative rules are applied. Only one character or creature can jump initiative.")],-1),ce=t("p",null,[t("strong",null,"Set Initiative for Enemies."),e(" Enemy initiative is always equal to 10 + their modifier (add or subtract 5 for advantage/disadvantage).")],-1),de=t("strong",null,"Exhaustion.",-1),ue=c("<h3>Class Changes</h3><p><strong>Artificer: Armor Model Increased Damage.</strong> Damage dice for Artificer Armorer&#39;s Armor Models is increased. Thunder Gauntlet damage increases to 1d10, and the Lightning Launcher&#39;s damage increases to 1d8. This is meant to be paired with the Advanced Weapons Table.</p><p><strong>Barbarian: Form of the Beast Increased Damage.</strong> Damage dice for Form of the Beast natural weapon attacks is increased. The tail and bite attacks do 1d10 damage, while the claws do 1d8. This is meant to be paired with the Advanced Weapons Table.</p><p><strong>Monk: Martial Arts.</strong> The Monks Martial Arts die increases in size by 1 die size. So a 1d4 becomes 1d6, 1d6 becomes 1d8, 1d8 becomes 1d10, and 1d10 becomes 1d12. If magic items would increase this further, 1d12 becomes 2d8, and 2d8 becomes 3d6. This is meant to be paired with the Advanced Weapons Table.</p><p><strong>Rogue: Psychic Blades</strong> The Rogue Soulknife&#39;s Psychic Blades feature has its damage increased to 1d8 on the main attack, and 1d6 for bonus action attacks. This is meant to be paired with the Advanced Weapons Table.</p><p><strong>Warlock: Spell Slots Increase.</strong> This homebrew change increases the spell slots of the warlock class to be equal to the characters proficiency bonus (for single class warlocks), or what their proficiency bonus would be if they only had their warlock levels (for multiclass warlocks). This is available in the character builder under Warlock (Joe&#39;s Edit) class.</p><p><strong>Warlock: Pact of the Blade/Hexblade Refactor.</strong> This change moves most of the Hexblade&#39;s level 1 bonuses to the Pact of the Blade feature. Specifically moved are: Charisma based weapon attacks with the Pact Weapon, proficiency with Medium Armor, shields, and martial weapons. Hexblades retain the Hex Curse ability as exclusive to them as their first level feature. Available in the Character Builder under Warlock (Joe&#39;s Edit)</p><p><strong>Wizard: Bladesinger Abilities Refactor.</strong> The Bladesong ability has been refactored. The changes are: 1. it no longer provides an AC bonus. It instead provides a bonus to weapon damage and temporary hit points. Song of Victory has been moved to 10th level and now provides an additional 1d8 force damage (instead of intelligence to damage). Song of Defense has been moved to 14th level and now provides the ability to recover temporary hit points on enemy death by weapon attack (instead of expending spell slots to reduce damage). These changes are available in the character builder as &quot;College of Bladesingers (homebrew)&quot;</p><h3>Feats</h3>",9),pe=t("strong",null,"Dual Flurry.",-1);function ge(fe,me,be,_e,ye,we){const a=r("router-link");return i(),h("div",null,[u,p,g,f,m,t("p",null,[b,e(" Characters may belong to one or more major factions in the world. These factions require the character to complete missions and act in their interest, but so long as the character remains in good standing with the faction, they also gain bonus features and abilities from being a member, including specific bonuses at character levels 1 and 10. See the "),o(a,{to:"/world/orgs"},{default:s(()=>[e("Major Organizations")]),_:1}),e(" page for details on individual factions, and I would encourage DMs to work with their players to create custom factions that fit what the character is looking for.")]),_,t("p",null,[y,e(" In most of my games we use an injury table to determine if characters have long lasting injuries from being knocked out in combat. The injury table can be found in the "),o(a,{to:"/ref/quick"},{default:s(()=>[e("Quick Reference Manual.")]),_:1})]),w,v,t("p",null,[k,e(" At certain points in a campaign the characters may get control of, or have the option to found, a castle or town. The "),o(a,{to:"/tools/stronghold"},{default:s(()=>[e("Stronghold Builder")]),_:1}),e(" is an online engine designed to create a uniform way to manage that castle or town.")]),S,C,T,R,A,t("p",null,[I,e(" I have created 5 homebrew cleric domains, Air, Earth, Fire, Trade, and Water. See the "),o(a,{to:"/options/domains"},{default:s(()=>[e("Divine Domains")]),_:1}),e(" page for details.")]),t("p",null,[x,e(" The Factotum is a homebrew class based on the D&D 3.5 class of the same name. See the "),o(a,{to:"/options/class"},{default:s(()=>[e("Character Classes")]),_:1}),e(" page for details.")]),t("p",null,[B,e(" The Scourer is a homebrew subclass for fighters designed by a good friend of mine specifically intended to counter mages. See the "),o(a,{to:"/options/class"},{default:s(()=>[e("Character Classes")]),_:1}),e(" page for details.")]),t("p",null,[M,e(" The Eldritch Knight can choose to use the Eldritch Knight spell list instead of a restricted version of the Wizard spell list. However, they must use one or the other for all of their selections, not both. See the "),o(a,{to:"/options/class"},{default:s(()=>[e("Character Classes")]),_:1}),e(" page for details.")]),t("p",null,[D,e(" The Hunter and the Beast Master ranger gain bonus spells known thematic to their subclass. See the "),o(a,{to:"/options/class"},{default:s(()=>[e("Character Classes")]),_:1}),e(" page for details.")]),t("p",null,[W,e(" All sorcerer subclasses have expanded spell lists thematic to their subclass that are known automatically and do not count against their maximum spells known. See the "),o(a,{to:"/options/class"},{default:s(()=>[e("Character Classes")]),_:1}),e(" page for details.")]),t("p",null,[E,e(" The Sorceron Bloodline is a homebrew subclass I developed particular to the setting. See the "),o(a,{to:"/options/class"},{default:s(()=>[e("Character Classes")]),_:1}),e(" page for details.")]),F,t("p",null,[H,e(" Wizards have additional spells known in the form of a domain style spell-list that is thematic to their subclass. These are automatically added to their spellbook, always prepared, and do not count against their maximum spells prepared. See the "),o(a,{to:"/options/class"},{default:s(()=>[e("Character Classes")]),_:1}),e(" page for details.")]),q,t("p",null,[P,e(" Characters (in general) do not gain languages based on their race. Rather they get them based on their home country. See the "),o(a,{to:"/options/races"},{default:s(()=>[e("Character Races")]),_:1}),e(" and "),o(a,{to:"/options/languages"},{default:s(()=>[e("Languages")]),_:1}),e(" page for details.")]),t("p",null,[z,e(" The character age tables have been heavily modified, specifically in the direction of making the super long-lived races a bit closer to human norms. No playable race has a maximum age greater than 190. See the "),o(a,{to:"/options/races"},{default:s(()=>[e("Character Races")]),_:1}),e(" page for details.")]),t("p",null,[j,e(" Children of the God of Lust and Fire. Their mothers may come from any race, but they themselves are almost universally examples of grace and beauty. See the "),o(a,{to:"/options/races"},{default:s(()=>[e("Character Races")]),_:1}),e(" page for details.")]),t("p",null,[G,e(" Dwarves have a custom set of subraces based on the setting. Some of these are based on existing subraces but may have been renamed. See the "),o(a,{to:"/options/races"},{default:s(()=>[e("Character Races")]),_:1}),e(" page for details.")]),t("p",null,[N,e(" Elves have a custom set of subraces based on the setting. Some of these are based on existing subraces but may have been renamed. See the "),o(a,{to:"/options/races"},{default:s(()=>[e("Character Races")]),_:1}),e(" page for details.")]),t("p",null,[K,e(" Chosen people of Gaian, closely related to the fey. See the "),o(a,{to:"/options/races"},{default:s(()=>[e("Character Races")]),_:1}),e(" page for details.")]),t("p",null,[L,e(" The gnome subraces have been renamed to match the setting. See the "),o(a,{to:"/options/races"},{default:s(()=>[e("Character Races")]),_:1}),e(" page for details.")]),t("p",null,[O,e(" Children of dwarves and humans. See the "),o(a,{to:"/options/races"},{default:s(()=>[e("Character Races")]),_:1}),e(" page for details.")]),t("p",null,[V,e(" Descendents of humans and halflings or elves and halflings, tallfellow halflings are medium instead of small. See the "),o(a,{to:"/options/races"},{default:s(()=>[e("Character Races")]),_:1}),e(" page for details.")]),$,t("p",null,[J,e(" Humans are split into 5 distinct custom subraces, none of which exactly match the default human in the PHB. See the "),o(a,{to:"/options/races"},{default:s(()=>[e("Character Races")]),_:1}),e(" page for details.")]),t("p",null,[Q,e(" Kobolds do not have the sunlight sensitivity feature. See the "),o(a,{to:"/options/races"},{default:s(()=>[e("Character Races")]),_:1}),e(" page for details.")]),t("p",null,[U,e(" The Orc race has been heavily modified, and has 3 custom subclasses. See the "),o(a,{to:"/options/races"},{default:s(()=>[e("Character Races")]),_:1}),e(" page for details.")]),t("p",null,[X,e(" Descendents of the ancient Sorceron people, these rare people (similar to the Aasimar) have a special proclivity for arcane magic. See the "),o(a,{to:"/options/races"},{default:s(()=>[e("Character Races")]),_:1}),e(" page for details.")]),t("p",null,[Y,e(" Subraces for Tieflings have been customized and rebuilt for the setting. See the "),o(a,{to:"/options/races"},{default:s(()=>[e("Character Races")]),_:1}),e(" page for details.")]),t("p",null,[Z,e(" Descendents of genetic experiments by the Sorceron to try to create the perfect soldier. See the "),o(a,{to:"/options/races"},{default:s(()=>[e("Character Races")]),_:1}),e(" page for details.")]),ee,t("p",null,[te,e(" A cantrip that enables you to make a weapon made of energy. See the "),o(a,{to:"/ref/quick"},{default:s(()=>[e("Quick Reference Manual")]),_:1}),e(" for details.")]),ae,oe,se,ne,t("p",null,[le,e(" The advanced weapons table is intended to make weapons "),re,e(". Each weapon is unique, and every attempt has been made to make them balanced against each other as well. What they are not is balanced against the "),ie,e(". As such this rule when implemented will be paired with the improved class spell weapon dice feature below. The Advanced Weapons Table can be found in the "),o(a,{to:"/ref/quick"},{default:s(()=>[e("Quick Reference Manual.")]),_:1})]),he,ce,t("p",null,[de,e(" In the "),o(a,{to:"/ref/quick"},{default:s(()=>[e("conditions section of the quick reference manual")]),_:1}),e(" you will find the exhaustion rule. It is a modified version of the one from the OneDND play test.")]),ue,t("p",null,[pe,e(" A feat meant to further enhance two-weapon fighting by granting a second off-hand attack when the correct conditions are met, the idea for this came from the Solasta Community Expansion mod. See the "),o(a,{to:"/options/feats"},{default:s(()=>[e("Feats")]),_:1}),e(" page for details.")])])}const ke=l(d,[["render",ge]]);export{ke as default};
